-- SkillSync Firebase Firestore Schema Reference
-- Note: Firebase uses collections (like tables) and documents (like rows)
-- This file documents the structure for reference

-- COLLECTIONS (conceptual schema)

-- 1. users
-- Document ID: uid (auto-generated by Firebase Auth)
-- Fields:
--   - email: string
--   - fullName: string
--   - role: "student" | "mentor" | "team_lead"
--   - bio: string
--   - avatar: string
--   - skills: array of { skillId: string, level: "beginner"|"intermediate"|"expert", endorsements: number }
--   - interests: array of strings
--   - joinedAt: timestamp
--   - availability: "available" | "busy" | "offline"
--   - preferences: { notifications: boolean, privateProfile: boolean }
--   - points: number
--   - badges: array of strings
--   - createdAt: timestamp
--   - updatedAt: timestamp

-- 2. skills
-- Document ID: custom (skill name slug)
-- Fields:
--   - name: string
--   - category: "frontend" | "backend" | "mobile" | "devops" | "design" | "data"
--   - description: string
--   - difficulty: "beginner" | "intermediate" | "expert"
--   - endorsements: number
--   - createdAt: timestamp

-- 3. teams
-- Document ID: auto-generated
-- Fields:
--   - name: string
--   - description: string
--   - leader: string (uid)
--   - members: array of { userId: string, role: "leader"|"developer"|"designer"|"manager", joinedAt: timestamp }
--   - requiredSkills: array of { skillId: string, level: string, priority: number }
--   - complementaryMatch: { score: number, details: object }
--   - project: string (projectId)
--   - status: "forming" | "active" | "completed" | "archived"
--   - createdAt: timestamp
--   - updatedAt: timestamp

-- 4. projects
-- Document ID: auto-generated
-- Fields:
--   - title: string
--   - description: string
--   - team: string (teamId)
--   - status: "planning" | "in_progress" | "completed" | "archived"
--   - startDate: timestamp
--   - endDate: timestamp
--   - tasks: array of { taskId: string, title: string, description: string, assignee: string, status: string, dueDate: timestamp }
--   - resources: array of { type: string, url: string, uploadedBy: string, uploadedAt: timestamp }
--   - createdAt: timestamp
--   - updatedAt: timestamp

-- 5. messages
-- Document ID: auto-generated
-- Fields:
--   - teamId: string
--   - sender: string (uid)
--   - content: string
--   - attachments: array of { type: string, url: string, name: string }
--   - reactions: { "emoji": [array of userIds] }
--   - createdAt: timestamp
--   - updatedAt: timestamp

-- 6. skillMatches (cached AI recommendations)
-- Document ID: userId
-- Fields:
--   - userId: string
--   - suggestedMatches: array of { userId: string, score: number, reason: string, commonSkills: array, complementarySkills: array }
--   - generatedAt: timestamp
--   - expiresAt: timestamp

-- 7. leaderboard
-- Document ID: auto-generated
-- Fields:
--   - userId: string
--   - points: number
--   - rank: number
--   - completedProjects: number
--   - teamsLed: number
--   - skillEndorsements: number
--   - contributionScore: number
--   - updatedAt: timestamp

-- 8. badges
-- Document ID: custom (badge name slug)
-- Fields:
--   - name: string
--   - description: string
--   - icon: string
--   - criteria: { type: string, threshold: number }
--   - createdAt: timestamp

-- 9. userBadges
-- Document ID: auto-generated
-- Fields:
--   - userId: string
--   - badgeId: string
--   - earnedAt: timestamp
